import "./App.css";
import React from "react";
import { Logo } from "./Logo";
import { Tests } from "./Tests";
import { Sections } from "./Sections";
import { Topics } from "./Topics";
import { defaultTests } from "./defaultTests";

export default function App() {
  const [tests, setTests] = React.useState(defaultTests);
  const [activeTest, setActiveTest] = React.useState(null);
  const [activeSection, setActiveSection] = React.useState(null);
  const activeTestObj = tests.find((test) => test.id === activeTest);
  const activeSectionObj = activeTestObj
    ? activeTestObj.sections.find((section) => section.id === activeSection)
    : null;

  function handleTestClick(testID) {
    setActiveTest(testID);
    setActiveSection(null);
  }
  function handleSectionClick(sectionID) {
    setActiveSection(sectionID);
  }
  function handleAddTest() {
    const newTest = {
      id: tests.length + 1,
      name: ``,
      sections: [],
    };
    setTests([...tests, newTest]);
  }
  function handleTestUpdate(newTest) {
    const updatedTests = tests.map((test) => {
      if (test.id === newTest.id) {
        return newTest;
      }
      return test;
    });
    setTests(updatedTests);
  }

  return (
    <div className="App">
      <Logo />
      <Tests
        tests={tests}
        handleClick={handleTestClick}
        activeTest={activeTest}
        activeSection={null}
        handleAddTest={handleAddTest}
        handleTestUpdate={handleTestUpdate}
      />
      {activeTestObj && (
        <Sections
          test={activeTestObj}
          handleClick={handleSectionClick}
          activeSection={activeSection}
          handleTestUpdate={handleTestUpdate}
        />
      )}
      {activeSectionObj && (
        <Topics
          test={activeTestObj}
          handleTestUpdate={handleTestUpdate}
          activeSection={activeSection}
        />
      )}
      <ExportButton tests={tests} fs={fs} />
    </div>
  );
}

function ExportButton({ tests, fs }) {
  function updateTestFile() {
    let data = JSON.stringify(tests);
    fs.writeFile(
      "/Users/yams/Documents/studyHome/studyhome/src/tests.json",
      data,
      (err) => {
        if (err) {
          console.error("Error writing file:", err);
        } else {
          console.log("File has been saved.");
        }
      }
    );
    return <button onClick={updateTestFile}>Export</button>;
  }
}
