import "./App.css";
import React from "react";
import { Logo } from "./Logo";
import { Tests } from "./Tests";
import { Sections } from "./Sections";
import { Topics } from "./Topics";
import { defaultTests } from "./defaultTests";

export default function App() {
  const [tests, setTests] = React.useState(defaultTests);
  const [activeTest, setActiveTest] = React.useState(null);
  const [activeSection, setActiveSection] = React.useState(null);
  const activeTestObj = tests.find((test) => test.id === activeTest);
  const activeSectionObj = activeTestObj
    ? activeTestObj.sections.find((section) => section.id === activeSection)
    : null;

  function handleTestClick(testID) {
    setActiveTest(testID);
    setActiveSection(null);
  }
  function handleSectionClick(sectionID) {
    setActiveSection(sectionID);
  }
  function handleAddTest() {
    const newTest = {
      id: tests.length + 1,
      name: ``,
      sections: [],
    };
    setTests([...tests, newTest]);
  }
  function handleTestUpdate(newTest) {
    const updatedTests = tests.map((test) => {
      if (test.id === newTest.id) {
        return newTest;
      }
      return test;
    });
    setTests(updatedTests);
  }

  return (
    <div className="App">
      <Logo />
      <Tests
        tests={tests}
        handleClick={handleTestClick}
        activeTest={activeTest}
        activeSection={null}
        handleAddTest={handleAddTest}
        handleTestUpdate={handleTestUpdate}
      />
      {activeTestObj && (
        <Sections
          test={activeTestObj}
          handleClick={handleSectionClick}
          activeSection={activeSection}
          handleTestUpdate={handleTestUpdate}
        />
      )}
      {activeSectionObj && (
        <Topics
          test={activeTestObj}
          handleTestUpdate={handleTestUpdate}
          activeSection={activeSection}
        />
      )}
      <div className="footer">
        <h3>Export and Import Tests</h3>
        <div>
          <ExportButton tests={tests} />
          <ImportButton setTests={setTests} />
        </div>
      </div>
    </div>
  );
}

function ExportButton({ tests }) {
  function updateTestFile() {
    let data = JSON.stringify(tests);
    let blob = new Blob([data], { type: "application/json" });
    let url = URL.createObjectURL(blob);
    let a = document.createElement("a");
    a.href = url;
    a.download = "tests.json";
    a.click();
  }
  return (
    <button className="export" onClick={updateTestFile}>
      <h2>Export</h2>
    </button>
  );
}

function ImportButton({ setTests }) {
  function handleFileChange(e) {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      const content = e.target.result;
      setTests(JSON.parse(content));
    };
    reader.readAsText(file);
  }
  return (
    <div className="import-container">
      <input className="import" type="file" onChange={handleFileChange} />
    </div>
  );
}
